import React, { Component } from 'react';
import { YMaps, Map, Placemark,RouteEditor } from "react-yandex-maps";


// const divStyle = {
//   marginTop:'20px',
//   width: '100%',
//   height:'300px'
// };
class OrderMap extends Component {
  
    renderMap=()=>{
        loadScript("https://api-maps.yandex.ru/2.1/?lang=ru_RU&amp;apikey=68c06675-02b2-48e6-8d34-c2252114f143&apm;lang=ru_RU")
    }

    render() {
       

        return (
            <div>
                выаыва
                {/* <YMaps query={query}>
                    <Map
                        style={divStyle}
                        defaultState={mapData}
                        instanceRef={(ref) => this.initMap(null, ref)}
                        onLoad={(ymap) => this.initMap(ymap, null)}>
                         <RouteEditor/> 
                        {this.coordinates.map((coordinate, idx) => <Placemark geometry={coordinate} key={idx}/>)}
                    </Map>
                </YMaps>
                <div onClick={() => this.initMap()}>TEST</div> */}

                <div id="map"></div>
            </div>
        );
    }
}

function loadScript(url){
    var index =window.document.getElementByTagName("script")[0]
    var script = window.document.createElement("script")
    script.src=url
    script.async=true
    script.defer=true
    index.parentNode.insertBefore(script,index)
}


export default OrderMap;
