import React, { Component } from 'react';
// import { YMaps, Map, Placemark,RouteEditor } from "react-yandex-maps";


const divStyle = {
  marginTop:'20px',
  width: '100%',
  height:'300px'
};

let map, infoWindow;

class OrderMap extends Component {
  
    
    componentDidMount(){
        this.renderMap()
    }
    renderMap=()=>{
        // loadScript("https://maps.googleapis.com/maps/api/js?key=AIzaSyBmB5k507NzQDwBKAgH-IFlCg3hKR9rq4E&callback=initMap")

        loadScript("https://api-maps.yandex.ru/2.1/?lang=ru_RU&amp;apikey=56f71f4e-547f-44ef-b402-25edf5e63f0d")
        window.initMap=this.initMap
    }

    initMap=()=>{

         map = new window.google.maps.Map(document.getElementById('map'),
      {center:{lat:54.51,lng:36.25},
    zoom:13}

      )

      infoWindow = new window.google.maps.InfoWindow();

      const locationButton = document.createElement("button");
      locationButton.textContent = "Pan to Current Location";
      locationButton.classList.add("custom-map-control-button");
      map.controls[window.google.maps.ControlPosition.TOP_CENTER].push(locationButton);
      locationButton.addEventListener("click", () => {
        // Try HTML5 geolocation.
        if (navigator.geolocation) {
          navigator.geolocation.getCurrentPosition(
            (position) => {
              const pos = {
                lat: position.coords.latitude,
                lng: position.coords.longitude,
              };
              infoWindow.setPosition(pos);
              infoWindow.setContent("Location found.");
              infoWindow.open(map);
              map.setCenter(pos);
            },
            () => {
              handleLocationError(true, infoWindow, map.getCenter());
            }
          );
        } 

      });

      
    }

    

    render() {
       

        return (
            <div>
                выаыва
                
                <div style={divStyle} id="map"></div>
            </div>
        );
    }
}

function loadScript(url){
    var index =window.document.getElementsByTagName("script")[0]
    var script = window.document.createElement("script")
    script.src=url
    script.async=true
    script.defer=true
    index.parentNode.insertBefore(script,index)
}
function handleLocationError(browserHasGeolocation, infoWindow, pos) {
    infoWindow.setPosition(pos);
    infoWindow.setContent(
      browserHasGeolocation
        ? "Error: The Geolocation service failed."
        : "Error: Your browser doesn't support geolocation."
    );
    infoWindow.open(map);
  }


export default OrderMap;
