import React, { Component } from 'react';
// import { YMaps, Map, Placemark,RouteEditor } from "react-yandex-maps";


const divStyle = {
  marginTop:'20px',
  width: '100%',
  height:'300px'
};

let map, infoWindow;

class OrderMap extends Component {
  
    
    componentDidMount(){
        this.renderMap()
    }
    renderMap=()=>{
        loadScript("https://maps.googleapis.com/maps/api/js?key=AIzaSyBmB5k507NzQDwBKAgH-IFlCg3hKR9rq4E&callback=initMap")

